import styled from 'styled-components';

export const Header = styled.div`
    font-family: serif;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    /* background-color: black; */
    height: 100vh;
    width: 100vw;
`;

export const Data = styled.div`
    min-height: 500px;
    overflow: auto;
    * {
        min-height: 500px;
        margin-bottom: 24px;
    }
`;

export const Form = styled.div`
    display: flex;
    align-items: center;
    height: 32px;
    margin-top: 16px;
`;

export const Logo = styled.img`
    height: 500px;
`;

export const Label = styled.label`
    font-size: 24px;
    color: red;
    margin: 0px;
`;

export const Input = styled.input`
    font-family: inherit;
    font-size: 24px;
    border: 1px solid #f0131e;
    border-radius: 3px 0 0 3px;
    padding-left: 4px;
    padding-right: 4px;
    margin-left: 4px;
    background: black;
    color: white;
    &:focus {
        outline: 0;
    }
`;

export const Button = styled.button`
    background-color: #f0131e;
    color: white;
    font-size: 24px;
    font-family: inherit;
    height: 32px;
    border-radius: 0 3px 3px 0;
    border: 1px solid #f0131e;
    cursor: pointer;
    &:focus {
        outline: 0;
    }
    &:active {
        background-color: #d8131e;
        border-color: #d8131e;
    }
`;
