import React, { useState } from 'react';
import logo from './mdclogo.jpg';
import * as Styled from './styled';

const [search, setSearch] = useState("");

function App() {
  return (
      <Styled.Header>
        <Styled.Logo src={logo} alt="Marvel logo" />
        <Styled.Form >
          <Styled.Label for="search">Search for someone in the Marvel or DC Universes: </Styled.Label>
          <Styled.Input type="text" id="search" required placeholder="Iron Man" onChange={setSearch(event.target.value)}></Styled.Input>
          <Styled.Button onClick="callAPI()">Search</Styled.Button>
        </Styled.Form>
      </Styled.Header>
  );
}

function callAPI() {
  var xhttp = new XMLHttpRequest();
  xhttp.onreadystatechange = function() {
      console.log("Here");
      if (this.readyState == 4 && this.status == 200) {
        console.log("Ereh");
        // document.getElementById("response").innerHTML = this.responseText;
      }
  };
  xhttp.open("GET", "https://superheroapi.com/api/1711939795607990/search/"+search, true);
  xhttp.send();
}

export default App;
