import React, { useState } from 'react';
import logo from './mdclogo.jpg';
import * as Styled from './styled';


function App() {
  const [search, setSearch] = useState("");

  function callAPI() {
    console.log(search);
    var xhttp = new XMLHttpRequest();
    xhttp.onreadystatechange = function() {
      if (this.readyState === 4 && this.status === 200) {
        xhttp.open("GET", "https://superheroapi.com/api.php/1711939795607990/search/" + search, true);
        xhttp.setRequestHeader("access-control-allow-origin", "*");
        xhttp.setRequestHeader("access-control-allow-headers", "authorization, access-control-allow-origin");
        xhttp.send();
        console.log(xhttp.getAllResponseHeaders());      }
    };

  }

  return (
      <Styled.Header>
        <Styled.Logo src={logo} alt="Marvel logo" />
        <Styled.Form >
          <Styled.Label>Search for someone in the Marvel or DC Universes: </Styled.Label>
          <Styled.Input type="text" id="search" required placeholder="Iron Man" onChange={event => setSearch(event.target.value)} />
          <Styled.Button onClick={() => callAPI(search)}>Search</Styled.Button>
        </Styled.Form>
        <div id="response"></div>
      </Styled.Header>
  );
}



export default App;
