import React, { useState, useEffect } from 'react';
import logo from './mdclogo.jpg';
import * as Styled from './styled';


function App() {
	const [search, setSearch] = useState("");
	const [data, setData] = useState([]);

	const APICall = () => {
		fetch(
			`https://superheroapi.com/api.php/1711939795607990/search/${search}`,
		)
		.then(response => response.json())
		.then(data => setData(data))
		.catch(error => console.log(error));
	};

	return (
		<Styled.Header>
			<Styled.Logo src={logo} alt="Marvel logo" />
			<Styled.Form>
				<Styled.Label>Search for someone in the Marvel or DC Universes: </Styled.Label>
				<Styled.Input type="text" id="search" placeholder="Iron Man" onChange={event => setSearch(event.target.value)} />
				<Styled.Button onClick={() => APICall()}type="submit">Search</Styled.Button>
			</Styled.Form>
			<Styled.Data>
				{data &&
					Object.keys(data).map(key => (
						key === "results" &&
							Object.keys(data[key]).map(foo => (
								<Styled.DataRow>{JSON.stringify(data[key][foo])}</Styled.DataRow>
							))
						)
					)
				}
			</Styled.Data>
		</Styled.Header>
	);
}



export default App;
