import React, { useState } from 'react';
import logo from './mdclogo.jpg';
import * as Styled from './styled';

const id = 1711939795607990;


function App() {
  const [search, setSearch] = useState("");

  function callAPI() {
    console.log(search);
    var xhttp = new XMLHttpRequest();
    // xhttp.onreadystatechange = function() {
    //     console.log("Here");
    //     if (this.readyState === 4 && this.status === 200) {
    //       console.log("Ereh");
    //       document.getElementById("response").innerHTML = this.responseText;
    //     }
    // };
    xhttp.open("GET", "http://superheroapi.com/api/" + id + "/" + search, true);
    xhttp.send();
  }

  return (
      <Styled.Header>
        <Styled.Logo src={logo} alt="Marvel logo" />
        <Styled.Form >
          <Styled.Label for="search">Search for someone in the Marvel or DC Universes: </Styled.Label>
          <Styled.Input type="text" id="search" required placeholder="Iron Man" onChange={event => setSearch(event.target.value)} />
          <Styled.Button onClick={() => callAPI(search)}>Search</Styled.Button>
        </Styled.Form>
      </Styled.Header>
  );
}



export default App;
